import * as d3 from "d3";
//import { useState } from "react";

async function loadData() {
  var fileFath = "data/Avocado_HassAvocadoBoard_20152023v1.0.1.csv"
  const data = await d3.csv(fileFath);
  return data
}

// [{
//   Date : data[0].Date,
//   AveragePrice : data[0].AveragePrice,
//   TotalVolume : data[0].TotalVolume,
//   plu4046 : data[0].plu4046,
//   plu4225 : data[0].plu4225,
//   plu4770 : data[0].plu4770,
//   TotalBags : data[0].TotalBags,
//   SmallBags : data[0].SmallBags,
//   LargeBags : data[0].LargeBags,
//   XLargeBags : data[0].XLargeBags,
//   type : data[0].type,
//   region : data[0].region,
// }]

function drawPieChart(data) {
    const width = 680;
    const height = 680;
    const margin = 40;
    const radius = Math.min(width, height) / 2 - margin;

    const svg = d3.select("body").append("svg")
    .attr("width", width)
    .attr("height", height)
    .append("g")
    .attr("transform", "translate(" + width / 2 + "," + height / 2 + ")");

    var color = d3.scaleOrdinal()
    .domain(data)
    .range(["#98abc5", "#8a89a6", "#7b6888"])

    var pie = d3.pie().value(function(d) {return d.value; })(data);

    svg
    .selectAll('whatever')
    .data(pie)
    .enter()
    .append('path')
    .attr('d', d3.arc()
        .innerRadius(0)
        .outerRadius(radius)
    )
    .attr('fill', function(d){ return(color(d.data.key)) })
    .attr("stroke", "black")
    .style("stroke-width", "2px")
    .style("opacity", 0.7)
}


export default function Chart3() {
    var acc = {};
    acc[0] = 0;
    acc[1] = 0;
    acc[2] = 0;
    loadData().then((data) => {
        var count = data.forEach((d) => {
            //console.log(Number(d.LargeBags));
            if(!isNaN(parseFloat(d.SmallBags))){
                acc[0] += parseFloat(d.SmallBags);
            }
            if(!isNaN(parseFloat(d.LargeBags))){
                acc[1] += parseFloat(d.LargeBags);
            }
            if(!isNaN(parseFloat(d.XLargeBags))){
                acc[2] += parseFloat(d.XLargeBags);
            }
    })
    console.log(acc);
    // var sortedKeys = Object.keys(count).sort((a, b) => Number(a) - Number(b));
