    //   sortedObj.push({AveragePrice: key, count: count[key]});
    // });
    // sortedObj = sortedObj.filter(d => d.count > 1);
    //drawPieChart(acc);
    return data[0];
  });

  return (
    <div>
    </div>
  )
}
