import * as d3 from "d3";
//import { useState } from "react";

async function loadData() {
  var fileFath = "data/Avocado_HassAvocadoBoard_20152023v1.0.1.csv"
  const data = await d3.csv(fileFath);
  //console.log(data);
  return data
}

// [{
//   Date : data[0].Date,
//   AveragePrice : data[0].AveragePrice,
//   TotalVolume : data[0].TotalVolume,
//   plu4046 : data[0].plu4046,
//   plu4225 : data[0].plu4225,
//   plu4770 : data[0].plu4770,
//   TotalBags : data[0].TotalBags,
//   SmallBags : data[0].SmallBags,
//   LargeBags : data[0].LargeBags,
//   XLargeBags : data[0].XLargeBags,
//   type : data[0].type,
//   region : data[0].region,
// }]

function drawChart(data) {
  const width = 640;
  const height = 400;
  const marginTop = 20;
  const marginRight = 20;
  const marginBottom = 30;
  const marginLeft = 40;

  const svg = d3.select("body").append("svg")
  .attr("width", width)
  .attr("height", height);

  const x = d3.scaleUtc()
    .domain(d3.extent(data, d => new Date(d.Date)))
    .range([marginLeft, width - marginRight]);

  const y = d3.scaleLinear()
    .domain([0, d3.max(data, d => d.AveragePrice)])
    .range([height - marginBottom, marginTop]);

    // Add the x-axis.
  svg.append("g")
  .attr("transform", `translate(0,${height - marginBottom})`)
  .call(d3.axisBottom(x));

  // Add the y-axis.
  svg.append("g")
    .attr("transform", `translate(${marginLeft},0)`)
    .call(d3.axisLeft(y));

  // Add the line.

  // Declare the line generator.
  const line = d3.line()
      .x(d => x(new Date(d.Date)))
      .y(d => y(d.AveragePrice));

  svg.append("path")
  .attr("fill", "none")
  .attr("stroke", "steelblue")
  .attr("stroke-width", 1.5)
  .attr("d", line(data));
}

function drawBarChart(data) {

  // for (let key in data) {
  //   if (data.hasOwnProperty(key)) { // 确保是对象自有属性，不是原型链上的属性
  //     console.log(key, data[key]);
  //   }
  // }
  const width = 640;
  const height = 400;
  const marginTop = 20;
  const marginRight = 20;
  const marginBottom = 30;
  const marginLeft = 40;

  const svg = d3.select("body").append("svg")
  .attr("width", width)
  .attr("height", height);

  const x = d3.scaleBand()
    .domain([0, data[data.length - 1].AveragePrice])
    .range([marginLeft, width - marginRight]);

  const y = d3.scaleLinear()
    .domain([0, d3.max(data, d => d.count)])
    .range([height - marginBottom, marginTop]);

    // Add the x-axis.
  svg.append("g")
  .attr("transform", `translate(0,${height - marginBottom})`)
  .call(d3.axisBottom(x));

  // Add the y-axis.
  svg.append("g")
    .attr("transform", `translate(${marginLeft},0)`)
    .call(d3.axisLeft(y));

  svg
  .append("g") //注6
  .attr("fill", 'royalblue') //注7
  .selectAll("rect") //注8
  .data(data) //注9
  .join("rect") //注10
        .attr("x", (d, i) => x(d.AveragePrice)) //注11
        .attr("y", d => y(d.count)) //注12
		.attr('height', d=> y(0) - y(d.count)) //注13
		.attr('width', x.bandwidth()); //注14
}

export default function Chart1() {
  loadData().then((data) => {
    var filteredDataForAlbanyConventional = data.filter(d => d.region === "Atlanta" && d.type === "conventional");
    var filteredDataForAlbanyOrganic = data.filter(d => d.region === "Atlanta" && d.type === "organic");
    var b1 = data.filter(d => d.region === "Chicago" && d.type === "conventional");
    var b2 = data.filter(d => d.region === "Chicago" && d.type === "organic");
    var count = data.reduce((acc, d) => {
      acc[d.AveragePrice] = (acc[d.AveragePrice] || 0) + 1;
      return acc;
    }, {});
    var sortedKeys = Object.keys(count).sort((a, b) => Number(a) - Number(b));
    var sortedObj = [];
    sortedKeys.forEach(key => {
      sortedObj.push({AveragePrice: key, count: count[key]});
    });
    drawBarChart(sortedObj);
    drawChart(filteredDataForAlbanyConventional);
    drawChart(filteredDataForAlbanyOrganic);
    drawChart(b1);
    drawChart(b2);
    return data[0];
  });

  return (
    <div>
    </div>
  )
}
