import * as d3 from "d3";
//import { useState } from "react";

async function loadData() {
  var fileFath = "data/Avocado_HassAvocadoBoard_20152023v1.0.1.csv"
  const data = await d3.csv(fileFath);
  return data
}

// [{
//   Date : data[0].Date,
//   AveragePrice : data[0].AveragePrice,
//   TotalVolume : data[0].TotalVolume,
//   plu4046 : data[0].plu4046,
//   plu4225 : data[0].plu4225,
//   plu4770 : data[0].plu4770,
//   TotalBags : data[0].TotalBags,
//   SmallBags : data[0].SmallBags,
//   LargeBags : data[0].LargeBags,
//   XLargeBags : data[0].XLargeBags,
//   type : data[0].type,
//   region : data[0].region,
// }]

function drawPieChart(data) {

    // set the dimensions and margins of the graph
    const width = 450,
        height = 450,
        margin = 40;

    // The radius of the pieplot is half the width or half the height (smallest one). I subtract a bit of margin.
    const radius = Math.min(width, height) / 2 - margin;

    // append the svg object to the div called 'my_dataviz'
    const svg = d3.select("#my_dataviz")
    .append("svg")
        .attr("width", width)
        .attr("height", height)
    .append("g")
        .attr("transform", `translate(${width/2}, ${height/2})`);

    // Create dummy data

    // set the color scale
    const color = d3.scaleOrdinal()
    .range(["#98abc5", "#8a89a6", "#7b6888"])

    // Compute the position of each group on the pie:
    const pie = d3.pie()
    .value(function(d) {return d[1]})
    const data_ready = pie(Object.entries(data))

    // Build the pie chart: Basically, each part of the pie is a path that we build using the arc function.
    svg
    .selectAll('whatever')
    .data(data_ready)
    .join('path')
    .attr('d', d3.arc()
        .innerRadius(0)
        .outerRadius(radius)
    )
    .attr('fill', function(d){ return(color(d.data[1])) })
    .attr("stroke", "black")
    .style("stroke-width", "2px")
    .style("opacity", 0.7)
}


export default function Chart3() {
    var acc = {};
    acc[0] = 0;
    acc[1] = 0;
    acc[2] = 0;
    loadData().then((data) => {
        var count = data.forEach((d) => {
            //console.log(Number(d.LargeBags));
            if(!isNaN(parseFloat(d.SmallBags))){
                acc[0] += parseFloat(d.SmallBags);
            }
            if(!isNaN(parseFloat(d.LargeBags))){
                acc[1] += parseFloat(d.LargeBags);
            }
            if(!isNaN(parseFloat(d.XLargeBags))){
                acc[2] += parseFloat(d.XLargeBags);
            }
    })
    console.log(acc);
    // var sortedKeys = Object.keys(count).sort((a, b) => Number(a) - Number(b));
    // var sortedObj = [];
    // sortedKeys.forEach(key => {
    //   sortedObj.push({AveragePrice: key, count: count[key]});
    // });
    // sortedObj = sortedObj.filter(d => d.count > 1);
    drawPieChart(acc);
    return data[0];
  });

  return (
    <div>
    </div>
  )
}
