import * as d3 from "d3";
import { useState } from "react";

async function loadData() {
  var fileFath = "data/test.csv"
  const data = await d3.csv(fileFath);
  return data
}

// [{
//   Date : data[0].Date,
//   AveragePrice : data[0].AveragePrice,
//   TotalVolume : data[0].TotalVolume,
//   plu4046 : data[0].plu4046,
//   plu4225 : data[0].plu4225,
//   plu4770 : data[0].plu4770,
//   TotalBags : data[0].TotalBags,
//   SmallBags : data[0].SmallBags,
//   LargeBags : data[0].LargeBags,
//   XLargeBags : data[0].XLargeBags,
//   type : data[0].type,
//   region : data[0].region,
// }]

function drawChart(data) {
    var margin = {top: 20, right: 20, bottom: 30, left: 50},
    width = 590 - margin.left - margin.right,
    height = 300 - margin.top - margin.bottom;

    var svg = d3.select("body").append("svg")
            .attr("width", width + margin.left + margin.right)
            .attr("height", height + margin.top + margin.bottom)
            .append("g")
            .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

    var parseDate = d3.time.format("%Y/%m/%d").parse;
    var x = d3.time.scale()
            .range([0, width]);

    var y = d3.scale.linear()
            .range([height, 0]);

    var xAxis = d3.svg.axis()
            .scale(x)
            .orient("bottom")
            .tickFormat(d3.time.format("%m.%d"));

    var yAxis = d3.svg.axis()
            .scale(y)
            .orient("left");

    x.domain(d3.extent(data, function(d) { return parseDate(d.Date); }));
    y.domain(d3.extent(data, function(d) { return d.AveragePrice; }));

    var line = d3.svg.line()
                .x(function(d) { return x(parseDate(d.Date)); })
                .y(function(d) { return y(d.AveragePrice); });
    svg.append("g")
            .attr("class", "x axis")
            .attr("transform", "translate(0," + height + ")")
            .call(xAxis);
    svg.append("g")
            .attr("class", "y axis")
            .call(yAxis)
            .append("text")
            .attr("transform", "rotate(0)")
            .attr("y", 25)
            .attr("x", 75)
            .attr("dy", ".71em")
            .style("text-anchor", "end")
            .text("我的数据");
    //定义纵轴网格线
    var yInner = d3.svg.axis()
    .scale(y)
    .tickSize(-width,0,0)
    .tickFormat("")
    .orient("left")
    .ticks(5);
    //添加纵轴网格线
    var yInnerBar=svg.append("g")
        .attr("class", "inner_line")
        .attr("transform", "translate(0,-25)")
        .call(yInner);

    svg.append("path")
            .datum(data)
            .attr("class", "line")
            .attr("d", line)
            .attr("opacity", 0)
            .transition()
            .duration(2000)
            .attr("opacity", 1);
    // 散点
    var points = svg.selectAll(".MyCircle")
    .data(data)
    .enter()
    .append("circle")
    .attr("class","MyCircle")
    .attr("transform","translate(0,0)")
    .attr("r", 3)
    .attr("opacity", 0)
    .transition()
    .duration(2000)
    .attr("cx", function(d){ return x(d.date); })
    .attr("opacity", 1)
    .attr("cy", function(d){ return y(d.close); });
}

export default function Chart2() {
  const [dataset, setState] = useState([]);
  var datas
  loadData().then((data) => {
    datas = data
    setState(JSON.stringify(data[0]));
    //console.log(data)
    drawChart(data);
    return data[0];
  });
  //drawChart(datas);
  return (
    <div>
      {dataset}
    </div>
  )
}
